void glbf_enqueue(pak,oif) { tdamp = pak.header.tdamp // damper value from packet prio = pak.header.prio // this hops packet prio pprio = pak.header.pprio // previous hops packet prior iif = pak.context.iif // incoming interface of packet
ta = adj_rcv_time(now(), \[1] pak.context.iif.speed, pak.context.length)
td = now() + ta // (dampened earliest) departure time pak.context.max1 = max1\[oif,prio] enqueue(pak, td, q(oif,iif,prio,pprio)) }

time_t adj_rcv_time(now, speed, length) {
  time_t now
  int speed, length

  return now - length * 8 / speed - FUDGE
}

void glbf_dequeue(oif) {
  next_packet: while(1) {
    tnow = now()
    ftd = tnow + 1  // time of first packet to send
    fq = NULL      // queue of first packet to send
    foreach prio in maxpriority...minpriority {
      foreach iif in iifs {
        foreach pprio in priorities}
          // find qhead with earliest departure time
          if (q = q(oif,iif,prio,pprio).qhead) {
            td = q.qhead.td   // target (departure) time
            if td < ftd
              ftd = td
              fq = q
            }
          }
        }
      }
      if fq { // found packet
        pak = dequeue(q,oif)
        glbf_send(oif,tnow,pak)
        break next_packet
      }
    }
  }
}

void glbf_send(oif,tnow,pak) {
  qtime = tnow - pak.context.td          // \[1]
  td = pak.context.max1 - qtime - FUDGE2 // \[2]
  pak.header.tdamp = td
  serialize(oif,pak)
}
